 import React from "react";
 import "./GSTTool.css";
 const GSTTool=()=> {
	const questions = [
		{
			questionText: 'State in which business is Started?',
			answerOptions: [
				{ answerText: 'Manipur, Mizoram, Nagaland and Tripura', isCorrect: false , isStateM4:true},
				{ answerText: 'Other states', isCorrect: true, isStateM4:false },
			
			],
		},
		{
			questionText: 'What is your turnover limit?',
			answerOptions: [
				{ answerText: 'Upto 10 lakhs', isCorrect: false,  isLimit10:true },
				{ answerText: 'Above 10 lakhs', isCorrect: true,  isLimit10:false },
			
			],
		},
		{
			questionText: 'Whether Supply is within state or Outside state?',
			answerOptions: [
				{ answerText: 'Within State', isCorrect: true , isSupplyWithinstate:true},
				{ answerText: 'Outside State' , isSupplyWithinstate:false },
				{ answerText: 'Both', issupplyBothstate :true },
				
			],
		},
		{

           
			questionText: 'Whether Supply of Goods or Service exclusively in?',
			answerOptions: [
				{ answerText: 'Non GST Supply', isCorrect: false },
				{ answerText: 'Exempt Supply/ Nil Rated Supply', isCorrect: false },
				{ answerText: 'Taxable', isCorrect: false },
				{ answerText: 'All of the Above', isCorrect: true },
			],
		},
        {

           
			questionText: 'Whether Supply of Service is Through E- commerce Operator Or Pay GST as E-commerce Operator?',
			answerOptions: [
				{ answerText: 'Yes', isCorrect: false },
				{ answerText: 'No', isCorrect: true },
				
			],
		},
        {

           
			questionText: 'Whether You are Non Resident Taxable Person Or Casual Taxable Person Or Input Service Distributor Or Agent of Another person?',
			answerOptions: [
                { answerText: 'Yes', isCorrect: true },
				{ answerText: 'No', isCorrect: false },
			],
		},

        {

           
			questionText: 'Whether you are required to Deduct Tax under GST (TDS Deductor) OR Collect Tax under GST (TCS Collector)?',
			answerOptions: [
                { answerText: 'Yes', isCorrect: true },
				{ answerText: 'No', isCorrect: false },
			],
		},
	];

const [currentQuestion, setCurrentQuestion] = React.useState(0);

const [showScore, setShowScore] = React.useState(false);

const [score, setScore] = React.useState(0);
const handleAnswerButtonClick = (isCorrect) => {
	if (isCorrect===true) {
		setScore(score + 1);
	}

	const nextQuestion = currentQuestion + 1;
	if(nextQuestion < questions.length) {
		setCurrentQuestion(nextQuestion);
	} else {
		setShowScore(true);
	}
};

	return (
		<div className='app'>
      {showScore ? (
				<div className='score-section'>You need GST Registration for your Business. </div>
			) : (
				<>
					<div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
						{questions[currentQuestion].answerOptions.map((answerOption) => (
						<button onClick={() => handleAnswerButtonClick(answerOption.isCorrect)}>{answerOption.answerText}</button>))}
					</div>
				</>
			)}
		</div>
	);
}

export default GSTTool;
